import React, { useState } from 'react';
import './App.css';

function App() {
  const busNumbers = ['5815', '5821', '6010', '6514', '6518', '6057', '8367', '6517'];
  const dates = Array.from({ length: 31 }, (_, i) => `${i + 1}/k`);
  const [bookings, setBookings] = useState({});

  const toggleBooking = (date, busNumber) => {
    setBookings(prev => {
      const key = `${date}-${busNumber}`;
      const newBookings = { ...prev };

      if (newBookings[key]) {
        delete newBookings[key];
      } else {
        newBookings[key] = true;
      }

      return newBookings;
    });
  };

  const isBooked = (date, busNumber) => {
    return bookings[`${date}-${busNumber}`];
  };

  return (
    <div className="container">
      <h1>Bus Booking Sheet</h1>
      <div className="table-container">
        <table className="booking-table">
          <thead>
            <tr>
              <th></th>
              {busNumbers.map(number => (
                <th key={number}>{number}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {dates.map(date => (
              <tr key={date}>
                <td className="date-cell">{date}</td>
                {busNumbers.map(busNumber => (
                  <td 
                    key={`${date}-${busNumber}`}
                    className={`booking-cell ${isBooked(date, busNumber) ? 'booked' : ''}`}
                    onClick={() => toggleBooking(date, busNumber)}
                  >
                    {isBooked(date, busNumber) ? '✓' : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
