import React, { useState } from 'react';
import './App.css';

function App() {
  const [bookings, setBookings] = useState([]);
  const [notification, setNotification] = useState('');

  const addBooking = (e) => {
    e.preventDefault();
    const date = e.target.date.value;
    const customer = e.target.customer.value;
    const busNumber = e.target.bus_number.value;

    // Check for duplicate booking
    const duplicate = bookings.some(
      b => b.date === date && b.busNumber === busNumber
    );

    if (duplicate) {
      setNotification(`Warning: Bus number ${busNumber} is already booked on ${date}!`);
    } else {
      setBookings([...bookings, { date, customer, busNumber }]);
      e.target.reset();
      setNotification('');
    }
  };

  const deleteBooking = (index) => {
    const newBookings = bookings.filter((_, i) => i !== index);
    setBookings(newBookings);
  };

  return (
    <div className="container">
      <h1>Bus Wedding Booking Sheet</h1>
      {notification && <div className="notification">{notification}</div>}

      <form onSubmit={addBooking} className="booking-form">
        <input type="date" name="date" required />
        <input type="text" name="customer" placeholder="Customer Name" required />
        <input type="text" name="bus_number" placeholder="Bus Number" required />
        <button type="submit">Add Booking</button>
      </form>

      <h2>Bookings</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Bus Number</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {bookings.length === 0 ? (
            <tr><td colSpan="4">No bookings yet.</td></tr>
          ) : (
            bookings.map((booking, index) => (
              <tr key={index}>
                <td>{booking.date}</td>
                <td>{booking.customer}</td>
                <td>{booking.busNumber}</td>
                <td>
                  <button 
                    onClick={() => deleteBooking(index)}
                    className="delete-btn"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default App;
