import React, { useState } from 'react';
import './App.css';

function App() {
  const busNumbers = ['5861', '2315', '6010', '5515', '6518', '6057', '8367', '6517'];
  const dates = Array.from({ length: 31 }, (_, i) => `${i + 1}/k`);
  const [bookings, setBookings] = useState([]);
  const [notification, setNotification] = useState('');

  const addBooking = (e) => {
    e.preventDefault();
    const date = e.target.date.value;
    const customer = e.target.customer.value;
    const busNumber = e.target.bus_number.value;
    const phone = e.target.phone.value;

    // Check for duplicate booking
    const duplicate = bookings.some(
      b => b.date === date && b.busNumber === busNumber
    );

    if (duplicate) {
      setNotification(`Warning: Bus number ${busNumber} is already booked on ${date}!`);
    } else {
      setBookings([...bookings, { date, customer, busNumber, phone }]);
      e.target.reset();
      setNotification('Booking added successfully!');
      setTimeout(() => setNotification(''), 3000);
    }
  };

  const deleteBooking = (index) => {
    const newBookings = bookings.filter((_, i) => i !== index);
    setBookings(newBookings);
    setNotification('Booking deleted successfully!');
    setTimeout(() => setNotification(''), 3000);
  };

  return (
    <div className="container">
      <h1>Bus Booking System</h1>

      {/* Booking Form */}
      <div className="form-container">
        <h2>Add New Booking</h2>
        {notification && <div className="notification">{notification}</div>}
        <form onSubmit={addBooking} className="booking-form">
          <div className="form-group">
            <label>Date:</label>
            <select name="date" required>
              {dates.map(date => (
                <option key={date} value={date}>{date}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Bus Number:</label>
            <select name="bus_number" required>
              {busNumbers.map(number => (
                <option key={number} value={number}>{number}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Customer Name:</label>
            <input type="text" name="customer" required placeholder="Enter customer name" />
          </div>
          <div className="form-group">
            <label>Phone Number:</label>
            <input type="tel" name="phone" required placeholder="Enter phone number" />
          </div>
          <button type="submit" className="submit-btn">Add Booking</button>
        </form>
      </div>

      {/* Bookings Table */}
      <div className="bookings-container">
        <h2>Current Bookings</h2>
        <table className="bookings-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Bus Number</th>
              <th>Customer Name</th>
              <th>Phone</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings.length === 0 ? (
              <tr>
                <td colSpan="5" className="no-bookings">No bookings available</td>
              </tr>
            ) : (
              bookings.map((booking, index) => (
                <tr key={index}>
                  <td>{booking.date}</td>
                  <td>{booking.busNumber}</td>
                  <td>{booking.customer}</td>
                  <td>{booking.phone}</td>
                  <td>
                    <button 
                      onClick={() => deleteBooking(index)}
                      className="delete-btn"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Calendar View */}
      <div className="calendar-container">
        <h2>Calendar View</h2>
        <div className="table-container">
          <table className="calendar-table">
            <thead>
              <tr>
                <th></th>
                {busNumbers.map(number => (
                  <th key={number}>{number}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {dates.map(date => (
                <tr key={date}>
                  <td className="date-cell">{date}</td>
                  {busNumbers.map(busNumber => {
                    const isBooked = bookings.some(
                      b => b.date === date && b.busNumber === busNumber
                    );
                    return (
                      <td 
                        key={`${date}-${busNumber}`}
                        className={`calendar-cell ${isBooked ? 'booked' : ''}`}
                        title={isBooked ? 'Booked' : 'Available'}
                      >
                        {isBooked ? '✓' : ''}
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default App;
